
create table blog_category (
	blog_cate_id INT PRIMARY KEY,
	name VARCHAR(15),
	count INT
);


create table blog (
	blog_id INT PRIMARY KEY,
	title VARCHAR(500),
	content TEXT,
	user_id INT,
	create_at DATE,
	blog_cate_id INT
);



create table blog_picture (
	blog_pic_id INT PRIMARY KEY,
	url VARCHAR(300),
	blog_id INT
);



